mapper.select:
/* selectPrPressList2 */ SELECT P.UID , P.PRESS_INDEX , P.TITLE , P.LANG , P.CONTENT , BA.NAME_KO AS BUSINESS_AREA_NAME_KO , BA.NAME_EN AS BUSINESS_AREA_NAME_EN , CONCAT('/upload/public/press/', F.PARENT_UID, '/', F.UID, '.', F.FILE_EXTENSION) AS ADDED_FILE , F.DESCRIPTION , DATE_FORMAT(P.REG_DATE, '%Y.%m.%d') AS REG_DATE FROM PRESS P INNER JOIN BUSINESS_AREA BA ON P.BUSINESS_AREA_INDEX = BA.BUSINESS_AREA_INDEX LEFT JOIN FILE F ON P.UID = F.PARENT_UID AND F.PARENT_TABLE = 'PRESS' WHERE LANG = #{lang} AND P.IS_PRESS = 'Y' AND P.IS_OPEN = 'Y'
AND TITLE LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY P.PRESS_INDEX DESC LIMIT #{offset}, #{limit}

mapper.update:
UPDATE PRESS
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE UID = #{uid}
